mapper.select:
select  ID
		,RESOURCEID
		,RESOURCENAME
		,INACCOUNTDATE
		,ORIVALUE
		,NETVALUE
		,ACCRUALOFDEPRECIATION
		,LIMITYEAR
		,KEEPER
		,KEEPERNAME
		,RESPONSIBILITYCENTER
		,SPEC
		,LSTOREOCATION
		,INVENTORYSITUATION
		,OTHERINFO
		,RESOURCESTATUS
		,RESOURCETYPE

   from resourcemanage


	and  ID=#{id}
	and  RESOURCEID=#{resourceid}
	and  RESOURCENAME=#{resourcename}
	and  INACCOUNTDATE=#{inaccountdate}
	and  ORIVALUE=#{orivalue}
	and  NETVALUE=#{netvalue}
	and  ACCRUALOFDEPRECIATION=#{accrualofdepreciation}
	and  LIMITYEAR=#{limityear}
	and  KEEPER=#{keeper}
	and  KEEPERNAME=#{keepername}
	and  RESPONSIBILITYCENTER=#{responsibilitycenter}
	and  SPEC=#{spec}
	and  LSTOREOCATION=#{lstoreocation}
	and  INVENTORYSITUATION=#{inventorysituation}
	and  OTHERINFO=#{otherinfo}
	and  RESOURCESTATUS=#{resourcestatus}
	and  RESOURCETYPE=#{resourcetype}

select *
 from resourcemanage


	and  ID=#{id}
	and  RESOURCEID=#{resourceid}
	and  RESOURCENAME=#{resourcename}
	and  INACCOUNTDATE=#{inaccountdate}
	and  ORIVALUE=#{orivalue}
	and  NETVALUE=#{netvalue}
	and  ACCRUALOFDEPRECIATION=#{accrualofdepreciation}
	and  LIMITYEAR=#{limityear}
	and  KEEPER=#{keeper}
	and  KEEPERNAME=#{keepername}
	and  RESPONSIBILITYCENTER=#{responsibilitycenter}
	and  SPEC=#{spec}
	and  LSTOREOCATION=#{lstoreocation}
	and  INVENTORYSITUATION=#{inventorysituation}
	and  OTHERINFO=#{otherinfo}
	and  RESOURCESTATUS=#{resourcestatus}
	and  RESOURCETYPE=#{resourcetype}

	select count(*)
      from resourcemanage 


		and  ID=#{id}
		and  RESOURCEID=#{resourceid}
		and  RESOURCENAME=#{resourcename}
		and  INACCOUNTDATE=#{inaccountdate}
		and  ORIVALUE=#{orivalue}
		and  NETVALUE=#{netvalue}
		and  ACCRUALOFDEPRECIATION=#{accrualofdepreciation}
		and  LIMITYEAR=#{limityear}
		and  KEEPER=#{keeper}
		and  KEEPERNAME=#{keepername}
		and  RESPONSIBILITYCENTER=#{responsibilitycenter}
		and  SPEC=#{spec}
		and  LSTOREOCATION=#{lstoreocation}
		and  INVENTORYSITUATION=#{inventorysituation}
		and  OTHERINFO=#{otherinfo}
		and  RESOURCESTATUS=#{resourcestatus}
		and  RESOURCETYPE=#{resourcetype}

mapper.update:
update resourcemanage
RESOURCEID=#{resourceid}, RESOURCENAME=#{resourcename}, INACCOUNTDATE=#{inaccountdate}, ORIVALUE=#{orivalue}, NETVALUE=#{netvalue}, ACCRUALOFDEPRECIATION=#{accrualofdepreciation}, LIMITYEAR=#{limityear}, KEEPER=#{keeper}, KEEPERNAME=#{keepername}, RESPONSIBILITYCENTER=#{responsibilitycenter}, SPEC=#{spec}, LSTOREOCATION=#{lstoreocation}, INVENTORYSITUATION=#{inventorysituation}, OTHERINFO=#{otherinfo}, RESOURCESTATUS=#{resourcestatus}, RESOURCETYPE=#{resourcetype},
ID=#{id}

mapper.delete:
delete from resourcemanage


		 and ID=#{id} 
		 and RESOURCEID=#{resourceid} 
		 and RESOURCENAME=#{resourcename} 
		 and INACCOUNTDATE=#{inaccountdate} 
		 and ORIVALUE=#{orivalue} 
		 and NETVALUE=#{netvalue} 
		 and ACCRUALOFDEPRECIATION=#{accrualofdepreciation} 
		 and LIMITYEAR=#{limityear} 
		 and KEEPER=#{keeper} 
		 and KEEPERNAME=#{keepername} 
		 and RESPONSIBILITYCENTER=#{responsibilitycenter} 
		 and SPEC=#{spec} 
		 and LSTOREOCATION=#{lstoreocation} 
		 and INVENTORYSITUATION=#{inventorysituation} 
		 and OTHERINFO=#{otherinfo} 
